            render={({ field }) => (
              <FormItem>
                <FormLabel>الوصف بالإنجليزي</FormLabel>
                <FormControl>
                  <Input placeholder="Example: Summer Season 26" {...field} data-testid="input-descriptionEn" />
                </FormControl>
                <FormMessage />
              </FormItem>
            )}
          />
        </>
      )}
      emptyMessage="لا توجد ترميزات. قم بإضافة ترميز جديد لتحويل الأكواد النصية إلى أرقام."
    />
  );
}

